// src/types/data.ts

// --- Enums ---
export enum JobType { 
  FULL_TIME = 'FULL_TIME', 
  PART_TIME = 'PART_TIME', 
  CONTRACT = 'CONTRACT' 
}

export enum JobStatus { 
  ACTIVE = 'ACTIVE', 
  PAUSED = 'PAUSED', 
  CLOSED = 'CLOSED' 
}

export enum UserRole { 
  SEEKER = 'SEEKER', 
  DAYCARE = 'DAYCARE', 
  RECRUITER = 'RECRUITER' 
}

// --- Interfaces ---
export interface Experience {
  id: string;
  title: string;
  company: string;
  startDate: string;
  endDate: string;
}

export interface Education {
  id: string;
  degree: string;
  institution: string;
  graduationYear: string;
}

export interface UserProfile {
  id: string;
  role: UserRole;
  firstName: string;
  lastName: string;
  email: string;
  location?: string;
  bio?: string;
  jobTitle?: string;
  skills?: string[];
  experience?: Experience[];
  education?: Education[];
  certifications?: string[];
  yearsOfExperience?: number;
  minSalary?: number;
  preferredJobTypes?: JobType[];
}

export interface Job {
  id: string;
  organizationId: string;
  organizationName: string;
  title: string;
  description: string;
  requirements: string[];
  location: string;
  salary: string;
  type: JobType;
  status: JobStatus;
}
